<div class="language-sql ext-sql line-numbers-mode"><pre v-pre class="language-sql"><code><span class="token comment">-- 创建ssm_db数据库</span>
<span class="token keyword">CREATE</span> <span class="token keyword">DATABASE</span> <span class="token keyword">IF</span> <span class="token operator">NOT</span> <span class="token keyword">EXISTS</span> ssm_db <span class="token keyword">CHARACTER</span> <span class="token keyword">SET</span> utf8<span class="token punctuation">;</span>

<span class="token comment">-- 使用ssm_db数据库</span>
<span class="token keyword">USE</span> ssm_db<span class="token punctuation">;</span>

<span class="token comment">-- 创建tbl_book表</span>
<span class="token keyword">CREATE</span> <span class="token keyword">TABLE</span> tbl_book<span class="token punctuation">(</span>
    id <span class="token keyword">INT</span> <span class="token keyword">PRIMARY</span> <span class="token keyword">KEY</span> <span class="token keyword">AUTO_INCREMENT</span><span class="token punctuation">,</span> <span class="token comment">-- 图书编号</span>
    <span class="token keyword">TYPE</span> <span class="token keyword">VARCHAR</span><span class="token punctuation">(</span><span class="token number">100</span><span class="token punctuation">)</span><span class="token punctuation">,</span> <span class="token comment">-- 图书类型</span>
    NAME <span class="token keyword">VARCHAR</span><span class="token punctuation">(</span><span class="token number">100</span><span class="token punctuation">)</span><span class="token punctuation">,</span> <span class="token comment">-- 图书名称</span>
    description <span class="token keyword">VARCHAR</span><span class="token punctuation">(</span><span class="token number">100</span><span class="token punctuation">)</span> <span class="token comment">-- 图书描述</span>
<span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token comment">-- 添加初始化数据</span>
<span class="token keyword">INSERT</span> <span class="token keyword">INTO</span> tbl_book <span class="token keyword">VALUES</span><span class="token punctuation">(</span><span class="token boolean">NULL</span><span class="token punctuation">,</span><span class="token string">'计算机理论'</span><span class="token punctuation">,</span><span class="token string">'Spring实战 第5版'</span><span class="token punctuation">,</span><span class="token string">'Spring入门经典教材，深入理解Spring原理技术内幕'</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token keyword">INSERT</span> <span class="token keyword">INTO</span> tbl_book <span class="token keyword">VALUES</span><span class="token punctuation">(</span><span class="token boolean">NULL</span><span class="token punctuation">,</span><span class="token string">'计算机理论'</span><span class="token punctuation">,</span><span class="token string">'Spring 5核心原理与30个类手写实战'</span><span class="token punctuation">,</span><span class="token string">'十年沉淀之作,手写Spring精华思想'</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token keyword">INSERT</span> <span class="token keyword">INTO</span> tbl_book <span class="token keyword">VALUES</span><span class="token punctuation">(</span><span class="token boolean">NULL</span><span class="token punctuation">,</span><span class="token string">'计算机理论'</span><span class="token punctuation">,</span><span class="token string">'Spring 5设计模式'</span><span class="token punctuation">,</span><span class="token string">'深入Spring源码剖析,Spring源码蕴含的10大设计模式'</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
